describe("request/response",function(){describe("when requesting a response",function(){var e,t;beforeEach(function(){e=new Wreqr.RequestResponse,e.setHandler("do:it",function(){return"some value"}),t=e.request("do:it")}),it("should return a value",function(){expect(t).toBe("some value")})}),describe("when requesting a response, with a parameter",function(){var e,t,n;beforeEach(function(){e=new Wreqr.RequestResponse,e.setHandler("do:it",function(e){n=e}),t=e.request("do:it","foo")}),it("should pass the param along",function(){expect(n).toBe("foo")})}),describe("when requesting a response, with multiple parameters",function(){var e,t,n,o;beforeEach(function(){e=new Wreqr.RequestResponse,e.setHandler("do:it",function(e,t){n=e,o=t}),t=e.request("do:it","foo","bar")}),it("should pass the param along",function(){expect(n).toBe("foo"),expect(o).toBe("bar")})})});