var mongoose = require('mongoose');

exports.user_shema = new mongoose.Schema({
  username : { type : String },
  password : { type : String },
  video : {
  	liked : { type : Number },
  	shared : { type : Number },
  	comments : { type : Number }
  }
});
exports.user = mongoose.model('user', exports.user_shema);

//////////////////////////////////////////////

exports.video_shema = new mongoose.Schema({
	userId: { type : String },
	source : { type : String },
	title : { type : String },
	desc : { type : String },  
  liked: { type : Number, default: 0 },
  date : { type: Date, default: Date.now },
  comments : [{
  	content : { type : String },
  	author : { type : String },
  	liked: { type : Number }
  }],
});
exports.video = mongoose.model('video', exports.video_shema);





